package main

// my intuition:
// 1. build a trie
// 2. given a word s, i find it in trie
// 3. some chars in s is marked as 'end' in trie
// 4. when we encountered an 'end' char in s at i then we find s[i+1:] in the trie

// 472 pass=44 ud=882/119 s=1072ms (too long) m=103.4mb
func findAllConcatenatedWordsInADict(words []string) []string {
	trie := &TrieNode{}
	for _, w := range words {
		trieInsert(trie, w)
	}
	trie.End = false // concatenated words = sum of >= 2 'shorter' words so "" makes no sense

	var result []string
	for _, w := range words {
		// if w is directly found in trie without any recursion then we know that w is not
		// consists of >=2 words
		// - depth = -1 => not found
		// - depth = 0  => w is found as an single word
		// - depth > 0  => w is consists of >=2 shorter words
		if depth := dfs(trie, w, 0); depth > 0 {
			// log.Printf("======> %s: %d", w, depth)
			result = append(result, w)
		}
	}
	return result
}

func dfs(root *TrieNode, s string, depth int) int {
	// log.Printf("dfs: s=%s", s)
	p := root
	for i := 0; i < len(s); i++ {
		// log.Printf("\t%s: %2d", s, i)
		if p.End {
			if d := dfs(root, s[i:], depth+1); d >= 0 {
				return d
			}
		}
		if p = p.Br[int(s[i] - 'a')]; p == nil {
			return -1
		}
	}
	if p.End {
		return depth
	}
	return -1
}

type TrieNode struct {
	Br  [128]*TrieNode // branches
	End bool
}

func trieInsert(tn *TrieNode, s string) {
	p := tn
	for i := 0; i < len(s); i++ {
		k := int(s[i] - 'a')
		if p.Br[k] == nil {
			p.Br[k] = &TrieNode{}
		}
		p = p.Br[k]
	}
	p.End = true
}

// v1537

// no used
func trieFind(tn *TrieNode, s string) bool {
	p := tn
	for i := 0; i < len(s); i++ {
		k := int(s[i] - 'a')
		if p.Br[k] == nil {
			return false
		}
	}
	return p.End
}

